gressType = \"static\",\n                total = 100,\n                index = name .. unitid,\n                npcid = npcid,\n                name = name,\n                lastUpdate = time(),\n            }\n        end\n        \n        allstates[unitid].value = math.floor(current / max * 100)\n        allstates[unitid].changed = true\n        allstates[unitid].lastUpdate = time()\n        local allDead = true\n        for _, state in pairs(allstates) do\n            allDead = allDead and state.value == 0\n        end\n        for unitid, state in pairs(allstates) do\n            if allDead then\n                state.show = false\n            elseif state.npcid ~= \"15802\" and time() - state.lastUpdate > 30 then\n                state.show = false\n            elseif state.npcid == \"15802\" and time() - state.lastUpdate > 60 then\n                state.show = false\n            elseif state.npcid ~= \"15802\" and state.value == 0 then\n                state.show = false\n            end\n        end\n        return true\n    elseif e == \"COMBAT_LOG_EVENT_UNFILTERED\" and arg2 == \"UNIT_DIED\" then\n        local _, _, _, _, _, npcid, unitid = strsplit(\"-\",arg8)\n        if allstates[unitid] then\n            C_ChatInfo.SendAddonMessage(\n                aura_env.prefix,\n                string.format(\"%s:%s:%s:%d:%d\",\n                    unitid,\n                    npcid,\n                    \"\",\n                    0,\n                    100\n                ),\n                aura_env.channel\n            )\n        end  \n    end\nend",
